for fruit in fruits:
    print(f"I like {fruit}.")

class Dog:
    def __init__(self, name, breed):
        self.name = name
        self.breed = breed

    def bark(self):
        return f'{self.name} says woof!'

my_dog = Dog('Buddy', 'Golden Retriever')
print(my_dog.bark())

def fibonacci_generator(max):
    a, b = 0, 1
    while a < max:
        yield a
        a, b = b, a + b

for num in fibonacci_generator(10):
    print(num, end=' ')

# Output: 0 1 1 2 3 5 8
